con:config.script:
import groovy.json.JsonSlurper

// Get the response from the previous step
def response = context.expand('${Retrieve all subjects#Response}')

// Parse the response with JsonSlurper
def jsonSlurper = new JsonSlurper()
def jsonObject = jsonSlurper.parseText( response )

// Get the number of elements available and save them to the runtime properties
nbr_subjects = jsonObject.size()
testRunner.testCase.testSteps["Runtime properties"].setPropertyValue("nbr_subjects", nbr_subjects.toString())

con:configuration.scriptText:
import groovy.json.JsonSlurper

// Get the response from the previous step
def response = messageExchange.response.responseContent

// Parse the response with JsonSlurper
def jsonSlurper = new JsonSlurper()
def jsonObject = jsonSlurper.parseText( response )

// Expected result
def expected_result = context.getTestCase().testSteps["Runtime properties"].getPropertyValue("nbr_subjects")
expected_result = expected_result.toInteger()
expected_result = expected_result + 1
def actual_result = (jsonObject.size())
assert expected_result == actual_result

import groovy.json.JsonSlurper

